import React, { useState } from "react";
import Book_manu from "./component/basics/BookStore";

const App = () => {
  const [myStyle, setMyStyle] = useState({
    color: "red",
    backgroundColor: "black",
  });

  const toggleStyle = () => {
    if (myStyle.color == "red") {
      setMyStyle({
        color: "red",
        backgroundColor: "#wheat",
      });
    } else {
      setMyStyle({
        color: "red",
        backgroundColor: "black",
      });
    }
  };

  return (
    <div style={myStyle}>
      <button onClick={toggleStyle}>Dark Mode</button>
      <Book_manu />
    </div>
  );
};

export default App;
