import React, { useState } from "react";
import Book_manu from "./component/basics/BookStore";
import Menu from "./component/basics/menuApi";
import Navbar from "./component/basics/Navbar";

const App = () => {
  const [myStyle, setMyStyle] = useState({
    color: "red",
    backgroundColor: "white",
  });

  const toggleStyle = () => {
    if (myStyle.backgroundColor === "white") {
      setMyStyle({
        color: "blue",
        backgroundColor: "black",
      });
    } else {
      setMyStyle({
        color: "red",
        backgroundColor: "white",
      });
    }
  };
  const uniqueList = [
    ...new Set(
      Menu.map((currElem) => {
        return currElem.category;
      })
    ),
    "All",
  ];

  console.log(uniqueList);

  const [menuData, setMenuData] = React.useState(Menu); //always add first.. it's a hook
  const [menuList, setMenuList] = React.useState(uniqueList);
  const filterItem = (category) => {
    if (category === "All") {
      setMenuData(Menu);
      return;
    }

    const updatedList = Menu.filter((curElem) => {
      return curElem.category === category;
    });
    setMenuData(updatedList);

    return (
      <div style={myStyle}>
        <label class="switch">
          <input type="checkbox" onClick={toggleStyle} />
          <span class="slider round"></span>
        </label>{" "}
        <Navbar filterItem={filterItem} menuList={menuList} />
        <MenuCard menuData={menuData} />
        <Book_manu />
      </div>
    );
  };
};
export default App;
